import React from 'react'; //dependency in node_modules
import ReactDom from 'react-dom';
import './index.css';

//Return single element wrapped
//section/article/fragment best
//use camelCase for html atributes
//close all the time
const books = [
  {
    id: 1,
    img: "https://m.media-amazon.com/images/I/71DY4WMA8fL._AC_UY436_FMwebp_QL65_.jpg",
    title: 'Red Rising Series',
    author: 'Pierce Brown',
    price: 58.00,
    pages: 4536
  },
  {
    id: 2,
    img: "https://m.media-amazon.com/images/I/A1kuBjgawsL._AC_UL640_FMwebp_QL65_.jpg",
    title: 'The Leaf Thief',
    author: 'Alice Hemming',
    price: 10.50,
    pages: 45
  },
  {
    id: 3,
    img: "https://m.media-amazon.com/images/I/71dzIGxxYBL._AC_UL640_FMwebp_QL65_.jpg",
    title: 'Right Now, I Am Kind',
    author: 'Daniela Owen',
    price: 14.75,
    pages: 62
  }
];
//props
let i = 0;

function BookList() {
  return (
    <>
      <section className="booklist"> {books.map((book, index) => {
        return <Book key={book.id} {...book}></Book>
      })}
      </section>
    </>
  );

}
// this is a component 
const Book = ({img, title, author, pages, price }) => {
  // attribute, eventHandler
  // onClick, onMouseOver
  const clickHandler = () => {
    alert('Hello World!');
  };
  const complexExample = (author) => {
    console.log(author);
  };
  return (
    <article className='book'>
      <img src={img} alt="" />
      <h1 onClick={() => console.log(title)}>{title}</h1> {/*inline function */}
      {/* {children} */}
      <h5>by {author.toUpperCase()}</h5>
      <h6>pages: {pages}</h6>
      <h6>${price}</h6>
      <button type="button" onClick={clickHandler}>Reference Example</button> {/* reference handler */}
      <button type="button" onClick={complexExample(author)}>Complex ex:</button> {/* complex example */}
    </article>
  );
};



ReactDom.render(<BookList />, document.getElementById('root'));
