import React from 'react'; //dependency in node_modules
import ReactDom from 'react-dom';
import './index.css';

//Return single element wrapped
//section/article/fragment best
//use camelCase for html atributes
//close all the time
const books = [
  {
    img: "https://m.media-amazon.com/images/I/71DY4WMA8fL._AC_UY436_FMwebp_QL65_.jpg",
    title: 'Red Rising Series',
    author: 'by Pierce Brown',
    price: 58.00,
    pages: 4536
  },
  {
    img: "https://m.media-amazon.com/images/I/A1kuBjgawsL._AC_UL640_FMwebp_QL65_.jpg",
    title: 'The Leaf Thief',
    author: 'by Alice Hemming',
    price: 10.50,
    pages: 45
  },
  {
    img: "https://m.media-amazon.com/images/I/71dzIGxxYBL._AC_UL640_FMwebp_QL65_.jpg",
    title: 'Right Now, I Am Kind',
    author: 'by Daniela Owen',
    price: 14.75,
    pages: 62
  }
];
//props
const names = ['john', 'peter', 'susan'];
const newNames = names.map()
function BookList() {
  return (
    <>
      <section className="booklist"> {names} </section>
    </>
  );

}

const Book = ({ img, title, author, pages, price, children }) => {
  return (
    <article className="book">
      <img src={img} alt="" />
      <h1>{title}</h1> {children}
      <h5>by {author.toUpperCase()}</h5>
      <h6>pages: {pages}</h6>
      <h6>${price}</h6>

    </article>
  );
}

ReactDom.render(<BookList />, document.getElementById('root'));
