import React from 'react'; //dependency in node_modules
import ReactDom from 'react-dom';
import './index.css';

//Return single element wrapped
//section/article/fragment best
//use camelCase for html atributes
//close all the time
const books = [
  {
    img: "https://m.media-amazon.com/images/I/71DY4WMA8fL._AC_UY436_FMwebp_QL65_.jpg",
    title: 'Red Rising Series',
    author: 'Pierce Brown',
    price: 58.00,
    pages: 4536
  },
  {
    img: "https://m.media-amazon.com/images/I/A1kuBjgawsL._AC_UL640_FMwebp_QL65_.jpg",
    title: 'The Leaf Thief',
    author: 'Alice Hemming',
    price: 10.50,
    pages: 45
  },
  {
    img: "https://m.media-amazon.com/images/I/71dzIGxxYBL._AC_UL640_FMwebp_QL65_.jpg",
    title: 'Right Now, I Am Kind',
    author: 'Daniela Owen',
    price: 14.75,
    pages: 62
  }
];
//props
let i = 0;

function BookList() {
  return (
    <>
      <section className="booklist"> {books.map((book) => {
        console.log(book);
        return (
          <article key={i++} className="book">
            <img src={book.img} alt="" />
            <h1>{book.title}</h1> {book.children}
            <h5>by {book.author.toUpperCase()}</h5>
            <h6>pages: {book.pages}</h6>
            <h6>${book.price}</h6>
          </article>
        )
      })}
      </section>
    </>
  );

}



ReactDom.render(<BookList />, document.getElementById('root'));
