import React from 'react'; //dependency in node_modules
import ReactDom from 'react-dom';
import './index.css';

//Return single element wrapped
//section/article/fragment best
//use camelCase for html atributes
//close all the time
const firstBook = {
  img: "https://m.media-amazon.com/images/I/71DY4WMA8fL._AC_UY436_FMwebp_QL65_.jpg",
  title: 'Red Rising Series',
  author: 'by Pierce Brown',
  price: 58.00,
  pages: 4536
}
const secondBook = {
  img: "https://m.media-amazon.com/images/I/A1kuBjgawsL._AC_UL640_FMwebp_QL65_.jpg",
  title: 'The Leaf Thief',
  author: 'by Alice Hemming',
  price: 10.50,
  pages: 45
}
const thirdBook = {
  img: "https://m.media-amazon.com/images/I/71dzIGxxYBL._AC_UL640_FMwebp_QL65_.jpg",
  title: 'Right Now, I Am Kind',
  author: 'by Daniela Owen',
  price: 14.75,
  pages: 62
}
const author = 'Pierce Brown';
//props

function BookList() {
  return (
    <>
      <section className="booklist">
        <Book img={firstBook.img} title={firstBook.title} author={firstBook.author} price={firstBook.price} pages={firstBook.pages} >
          <p>lorem25</p>
        </Book>
        <Book img={secondBook.img} title={secondBook.title} author={secondBook.author} price={secondBook.price} pages={secondBook.pages} />
        <Book img={thirdBook.img} title={thirdBook.title} author={thirdBook.author} price={thirdBook.price} pages={thirdBook.pages} />
        <Book img={firstBook.img} title={firstBook.title} author={firstBook.author} price={firstBook.price} pages={firstBook.pages} />
        <Book img={secondBook.img} title={secondBook.title} author={secondBook.author} price={secondBook.price} pages={secondBook.pages} />
        <Book img={thirdBook.img} title={thirdBook.title} author={thirdBook.author} price={thirdBook.price} pages={thirdBook.pages} />        <Book img={firstBook.img} title={firstBook.title} author={firstBook.author} price={firstBook.price} pages={firstBook.pages} />
        <Book img={secondBook.img} title={secondBook.title} author={secondBook.author} price={secondBook.price} pages={secondBook.pages} />
        <Book img={thirdBook.img} title={thirdBook.title} author={thirdBook.author} price={thirdBook.price} pages={thirdBook.pages} />
      </section>
    </>
  );

}

function Book(props) {
  return (
    <article className="book">
      <img src={props.img} />
      <h1>{props.title}</h1>
      <h5>by {props.author.toUpperCase()}</h5>
      <h6>pages: {props.pages}</h6>
      <h6>${props.price}</h6>
    </article>
  );
}
function Image() {
  return <img style={{ width: 10 + "em" }, { height: 15 + "em" }} src="https://m.media-amazon.com/images/I/71DY4WMA8fL._AC_UY436_FMwebp_QL65_.jpg" alt="" />
}
function Title() {
  return <h1>Red Rising</h1>
}
function Author() {
  return <h5 style={{ color: '#614298', fontSize: '0.75rem', margingTop: '0.25rem' }}>Pierce Brown</h5>
}
function Price() {
  return (
    <>
      <h6>Price: $58.00</h6>

    </>
  )
}

function Greeting() {

  return (
    // <> is a react fragment being returned  
    <>
      <div>
        <h4>Hello World!</h4>
        <ul>
          <li>
            <a href="#">hello Friends</a>
          </li>
          <li>
            <a href="#">hello Enemies</a>
          </li>
          <li>
            <Person />
          </li>
        </ul>
      </div>
    </>
  );

}

const Person = () => <h2> john doe</h2>;//this is nested

ReactDom.render(<BookList />, document.getElementById('root'));
